package com.sec.vd.skel;

import com.sec.vd.skel.redisconfig.RedisConfiguration;
import io.lettuce.core.RedisClient;
import io.lettuce.core.api.reactive.RedisReactiveCommands;
import io.lettuce.core.codec.ByteArrayCodec;
import io.lettuce.core.codec.RedisCodec;
import io.lettuce.core.codec.StringCodec;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.MediaType;
import org.springframework.stereotype.Component;
import org.springframework.web.reactive.function.server.ServerRequest;
import org.springframework.web.reactive.function.server.ServerResponse;
import reactor.core.publisher.Mono;
import reactor.core.scheduler.Schedulers;

import java.nio.charset.StandardCharsets;
import java.util.function.BiFunction;

@Component
public class SkelHandler {
    private static String key = "bigpayload";
    private static String bigpayload = "";

    public Mono<ServerResponse> ping(ServerRequest request)
    {
        return ServerResponse.ok().contentType(MediaType.TEXT_PLAIN).bodyValue(bigpayload);
    }



    @Autowired
    private RedisConfiguration redisConfiguration;

    public Mono<ServerResponse> ping_redis(ServerRequest request)
    {
        Mono<String> a = Mono.defer(() -> redisConfiguration.set(key,bigpayload.getBytes(StandardCharsets.UTF_8)));
        Mono<byte[]> b = Mono.defer(() -> redisConfiguration.get(key));
        return a.then(b).flatMap(ret ->	ServerResponse.ok().contentType(MediaType.TEXT_PLAIN).bodyValue(ret));
    }

}
